import { useState, useEffect } from 'react'
import axios from 'axios'

export default function ClientesList() {
  // Variáveis de estado, e como conterão dados precisam 
  //ser inicializados como vetores vazios 
  const [clientes, setClientes] = useState([])

  useEffect(() => {
    async function getData() {
      try { // tenta buscar os dados 
        let data = await axios.get('https://api.faustocintra.com.br/clientes')
        setClientes(data)
      }
      catch (error) {
        console.error(error)
      }
    }
    getData()
  }, []) // vetor vazio para o useEffect se executado apenas 
  // uma vez, no carregamento inicial do componente
  return (
    <>
      <h1>Lista de Clientes</h1>
      <div>
        {JSON.stringify(clientes)}
      </div>
    </>

  )
}
